import * as React from "react";
import { useTable } from "react-table";

const borderStyle = {
    border: "1px solid white"
};

export function ReactTable(jsonRows, jsonColumns) {
    let rowData = jsonRows.jsonRows
    let columnData = jsonRows.jsonColumns
    console.log(columnData)
    console.log(jsonRows.jsonRows);
    console.log(jsonRows.jsonColumns)
    const origData = [
        {
            actor: "Johnny Depp",
            movies: [
                {
                    name: "Pirates of the Carribean 1"
                },
                {
                    name: "Pirates of the Carribean 2"
                },
                {
                    name: "Pirates of the Carribean 3"
                },
                {
                    name: "Pirates of the Carribean 4"
                }
            ]
        }
    ];
    const newData = [];
    origData.forEach((actorObj) => {
        actorObj.movies.forEach((movie) => {
            newData.push({
                ["actor"]: actorObj.actor,
                movie: movie.name
            });
        });
    });



    let data = React.useMemo(() => rowData, []);

    console.log(data);

    const testJson = ['nazev', 'trivialni', 'grafika', 'pokrocile', 'jine']

    let testColumns = []
    columnData.forEach((element, index) => {
        let testObj = {
            Header: element,
            accessor: element,
            id: index
        }
        testColumns.push(testObj)
    });

    let columns = React.useMemo(
        () => testColumns,
        []
    );
    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow
    } = useTable({ columns, data });

    function read(e, index, row) {
        console.log(e);
    }
    return (
        <table {...getTableProps()} className='table table-bordered table-dark'>
            <thead>
                {headerGroups.map((headerGroup) => (
                    <tr {...headerGroup.getHeaderGroupProps()}>
                        {headerGroup.headers.map((column) => (
                            <th {...column.getHeaderProps()} style={borderStyle}>
                                {column.render("Header")}
                            </th>
                        ))}
                    </tr>
                ))}
            </thead>
            <tbody {...getTableBodyProps()}>
                {rows.map((row, i) => {
                    prepareRow(row);
                    return (
                        <tr {...row.getRowProps()}>
                            {row.cells.map((cell, j) => {
                                return (
                                    <td
                                        rowSpan={cell.rowSpan}
                                        onClick={() =>
                                            console.log(cell.column.id, row.cells[0].value)
                                        }
                                        {...cell.getCellProps()}
                                        style={borderStyle}
                                    >
                                        {cell.render("Cell")}
                                    </td>
                                );
                            })}
                        </tr>
                    );
                })}
            </tbody>
        </table>
    );
}
