import React, { useEffect, useState } from 'react';
import { Button } from "@material-ui/core";
import '../App.css'
import 'react-tabulator/lib/styles.css';
import { ReactTabulator } from 'react-tabulator'

export function ReadCsv() {

    let file = null;
    function fileInput() {
        file = this.files[0]
    }
    function submitFile() {
        if (!file || file.type !== 'text/csv') {
            alert('Vyberte spravný CSV file')
            return
        }
        let formData = new FormData()
        formData.append('file', file)
    }




    return (
        <div class="container py-4">
            <h1>Nahrát CSV</h1>
            <label for='file'>Vyberte file</label>
            <input type='file' className="form-control" name='file' id='file' onChange={fileInput} />
            <button class="btn btn primary mt-2" id="submitFile" onClick={submitFile}>Read</button>
            <div class="mt-2" id="csvTable"></div>
        </div>

    )
}
