import React, { useEffect, useState } from 'react';

// import './App.css'
import 'react-tabulator/lib/styles.css';
import { ReactTabulator } from 'react-tabulator'
import { Tabulator } from 'tabulator-tables';


export function ReadCsv() {
    const [tableRows, setTableRows] = useState([]);

    //State to store the values
    const [values, setValues] = useState([]);



    let file = null;
    function fileInput(e) {
        file = e.target.files[0]
        // console.log(e.target.files[0]);
    }
    function submitFile() {

        let formData = new FormData()
        formData.append('file', file);

        const fetchItems = async () => fetch("/api/tweets", {
            // mode: 'no-cors',
            method: 'POST',
            body: formData
        })
            .then((response) => response.json())
            .then(({ data }) => {
                if (data.length) {

                    console.log(data[0], data);
                    data[0].push("jine");

                    data.forEach((element, index) => {
                        if (index == 0) {
                            return
                        }
                        element.push("0")
                    })

                    let allArrays = data.slice(1, data.length);

                    setTableRows(data[0]);
                    setValues(allArrays);

                    console.log(tableRows);
                    console.log(values)
                    const columns = data[0]
                    /* Converting the array of arrays into an array of objects. */
                    window.rows = data.splice(1).map((arr) => {

                        const obj = {}
                        columns.forEach((column, index) => {
                            obj[column] = arr[index]
                        })
                        return obj
                    })

                    /* Adding a new property to each object in the array. */
                    window.rows.forEach(element => {
                        element.jine = "0"
                    });
                    console.log(window.rows);

                    // console.log(rows, columns)

                    const table = new Tabulator("#csvTable", {
                        height: "300px",
                        data: window.rows,
                        autoColumns: true
                    });
                } else {
                    alert('The CSV is empty')
                }
            })
            .catch((e) => alert(e.message))
        fetchItems()
    }
    function tableClick(e) {
        console.log(e)
        console.log(e.target.innerText)
        console.log()
    }


    // const data = require("./data");

    function SpanTable() {
        if (window.rows === undefined) {
            return
        }
        const dataImport = window.rows[0]

        function renderHeaderCells() {
            let headerCells = [];

            Object.keys(dataImport).map((x, i) => {
                let items = Object.values(dataImport)[i];
                headerCells.push(
                    <th colSpan={Object.keys(items).length} key={i.name}>
                        {x}
                    </th>
                );
                return headerCells;
            });
            return headerCells;
        }

        function renderSubHeaders() {
            let subHeaders = [];
            let subs = Object.values(dataImport);

            subs.map((x, i) => {
                if (subs[i] !== undefined) {
                    Object.keys(subs[i]).map(y => {
                        subHeaders.push(<td>{y}</td>);
                        return subHeaders;
                    });
                }
                return subHeaders;
            });
            return subHeaders;
        }

        function renderResults() {
            let results = [];
            let res = Object.values(dataImport);

            res.map((x, i) => {
                if (res[i] !== undefined) {
                    Object.values(res[i]).map(y => {
                        results.push(<td>{y}</td>);
                        return results;
                    });
                }
                return results;
            });

            return results;
        }

        return (
            <React.Fragment>
                <p>Fruit Basket</p>
                <table className='table table-bordered table-dark'>
                    <thead>
                        <tr>{renderHeaderCells()}</tr>
                    </thead>
                    <tbody>
                        <tr>{renderSubHeaders()}</tr>
                        <tr>{renderResults()}</tr>
                    </tbody>
                </table>
            </React.Fragment>
        );
    };

    function CreateTable() {
        return (
            <table className='table table-bordered table-dark'>
                <thead>
                    <tr>
                        {tableRows.map((rows, index) => {
                            return <th key={index}>{rows}</th>;
                        })}
                    </tr>
                </thead>
                <tbody>
                    {values.map((value, index) => {
                        return (
                            <tr key={index}>
                                {value.map((val, i) => {
                                    return <td onClick={tableClick} key={i}>{val}</td>;
                                })}
                            </tr>
                        );
                    })}
                </tbody>
            </table>
        )
    }

    return (
        <div className="container py-4">
            <h1>NahrÃ¡t CSV</h1>
            <label htmlFor='file'>Vyberte file</label>
            <input type='file' className="form-control" name='file' id='file' onChange={fileInput} />
            <button className="btn btn primary mt-2" id="submitFile" onClick={submitFile}>Read</button>
            <div className="mt-2" id="csvTable"></div>

            {/* <h1>{tableRows} and {values}</h1> */}
            <CreateTable />
            <SpanTable />
        </div>

    )
}
