import React, { useEffect, useState } from 'react';

// import './App.css'
import 'react-tabulator/lib/styles.css';
import { ReactTabulator } from 'react-tabulator'
import { Tabulator } from 'tabulator-tables';
import { ReactTable } from './components/react-table';
import { useTable } from 'react-table'


export function ReadCsv() {
    const [tableRows, setTableRows] = useState([]);

    //State to store the values
    const [values, setValues] = useState([]);



    let file = null;
    function fileInput(e) {
        file = e.target.files[0]
        // console.log(e.target.files[0]);
    }
    function submitFile() {

        let formData = new FormData()
        formData.append('file', file);

        const fetchItems = async () => fetch("/api/tweets", {
            // mode: 'no-cors',
            method: 'POST',
            body: formData
        })
            .then((response) => response.json())
            .then(({ data }) => {
                if (data.length) {
                    data.forEach((element, index) => {
                        if (index == 0) {
                            return
                        } console.log(element);
                    })
                    console.log(data[1], data);
                    data[0].push("jine");

                    data.forEach((element, index) => {
                        if (index == 0) {
                            return
                        }
                        element.push("0")
                    })

                    let allArrays = data.slice(1, data.length);

                    setTableRows(data[0]);
                    setValues(allArrays);

                    console.log(tableRows);
                    console.log(values)
                    const columns = data[0]
                    /* Converting the array of arrays into an array of objects. */
                    window.rows = data.splice(1).map((arr) => {

                        const obj = {}
                        columns.forEach((column, index) => {
                            obj[column] = arr[index]
                        })
                        return obj
                    })

                    /* Adding a new property to each object in the array. */
                    window.rows.forEach(element => {
                        element.jine = "0"
                    });
                    console.log(window.rows);

                } else {
                    alert('The CSV is empty')
                }
            })
            .catch((e) => alert(e.message))
        fetchItems()
    }


    const borderStyle = {
        border: "1px dashed navy"
    };

    function ReactTable() {
        const origData = [
            {
                actor: "Johnny Depp",
                movies: [
                    {
                        name: "Pirates of the Carribean 1"
                    },
                    {
                        name: "Pirates of the Carribean 2"
                    },
                    {
                        name: "Pirates of the Carribean 3"
                    },
                    {
                        name: "Pirates of the Carribean 4"
                    }
                ]
            }
        ];
        const newData = [];
        origData.forEach((actorObj) => {
            actorObj.movies.forEach((movie) => {
                newData.push({
                    ["ac tor"]: actorObj.actor,
                    movie: movie.name
                });
            });
        });
        const data = React.useMemo(() => newData, []);
        console.log(newData);
        const columns = React.useMemo(
            () => [
                {
                    Header: "Actor",
                    accessor: "actor",
                    id: "0"
                },
                {
                    Header: "Movies",
                    accessor: "movie",
                    id: "1"
                }
            ],
            []
        );
        const {
            getTableProps,
            getTableBodyProps,
            headerGroups,
            rows,
            prepareRow
        } = useTable({ columns, data });

        function read(e, index, row) {
            console.log(e);
        }
        return (
            <table {...getTableProps()}>
                <thead>
                    {headerGroups.map((headerGroup) => (
                        <tr {...headerGroup.getHeaderGroupProps()}>
                            {headerGroup.headers.map((column) => (
                                <th {...column.getHeaderProps()} style={borderStyle}>
                                    {column.render("Header")}
                                </th>
                            ))}
                        </tr>
                    ))}
                </thead>
                <tbody {...getTableBodyProps()}>
                    {rows.map((row, i) => {
                        prepareRow(row);
                        return (
                            <tr {...row.getRowProps()}>
                                {row.cells.map((cell, j) => {
                                    return (
                                        <td
                                            rowSpan={cell.rowSpan}
                                            onClick={() =>
                                                console.log(cell.column.id, row.cells[0].value)
                                            }
                                            {...cell.getCellProps()}
                                            style={borderStyle}
                                        >
                                            {cell.render("Cell")}
                                        </td>
                                    );
                                })}
                            </tr>
                        );
                    })}
                </tbody>
            </table>
        );
    }



    return (
        <div className="container py-4">
            <h1>Nahr√°t CSV</h1>
            <label htmlFor='file'>Vyberte file</label>
            <input type='file' className="form-control" name='file' id='file' onChange={fileInput} />
            <button className="btn btn primary mt-2" id="submitFile" onClick={submitFile}>Read</button>
            {/* <div className="mt-2" id="csvTable"></div> */}
            <ReactTable />
            {/* <h1>{tableRows} and {values}</h1> */}
            {/* <CreateTable /> */}

        </div>

    )
}





